mapper.select:
SELECT * FROM (
	SELECT ROWNUM AS idx, b.* FROM (
		SELECT * FROM beer 
		WHERE beername IS NOT NULL

			AND country in

			    #{country}



		     AND abv >= #{minabv} AND abv <= #{maxabv} 


			AND type in

			    #{type}


		ORDER BY beerno) b
	WHERE ROWNUM <= #{idx} + 30)
WHERE idx > #{idx}

SELECT DISTINCT b.beerNo, beerName, beerPic, company, country, type, abv, rating, ratingBA, note
FROM beer b, hashtag h
WHERE b.beerNo = h.beerNo AND tagname Like #{keyword}

mapper.update:

UPDATE beer
SET rating = #{rating}
WHERE beerNo = #{beerNo}

mapper.select:
SELECT count(*)
FROM beer_like
WHERE beerNo = #{beerNo} AND uuid = #{uuid}

SELECT count(*)
FROM beer_like
WHERE beerNo = #{beerNo}
